import java.util.*;

class Solution {
    public long solution(long n) {
        long answer = -1;
        double result = Math.sqrt(n);
        int num = (int) result;
        if( result == num )
            answer = (long) (num + 1) * (num + 1);
        return answer;
    }
}
```

