        releaseCount = 0;
        // 하루에 한바퀴씩 돌면서 진도 체크
        for(index = 0; index < progresses.length; index = index + 1){
            if( progresses[index] < 100 ){
                progresses[index] += speeds[index];
            }
        }
        // 앞의 기능이 완료 됐는지를 확인해가면서 카운팅
        while(progresses[0] >= 100){
            releaseCount = releaseCount + 1;

            // 카운팅이 끝난건 리스트에서 빼주자
            progresses.shift();
            speeds.shift();
        }

        if ( releaseCount > 0 ) {
            answer.push(releaseCount);
        }
    }
